import React, {useEffect} from 'react';
import clsx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import Drawer from '@material-ui/core/Drawer';
import Box from '@material-ui/core/Box';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import List from '@material-ui/core/List';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import Badge from '@material-ui/core/Badge';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Link from '@material-ui/core/Link';
import MenuIcon from '@material-ui/icons/Menu';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import NotificationsIcon from '@material-ui/icons/Notifications';
import { mainListItems, secondaryListItems } from './listitems';
import DaysWon from './DaysWon/DaysWon';
import LastResult from './LastResult/LastResult';
import PreviousResults from './PreviousResults/PreviousResults';
import SubmitGuess from './SubmitGuess/SubmitGuess';
import {connect} from 'react-redux';
import * as actions from '../../store/actions/index';
import Button from '@material-ui/core/Button';
import { useAuth0 } from "@auth0/auth0-react";

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="https://kovidr.ie/">
        KovidR
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  toolbar: {
    paddingRight: 24, // keep right padding when drawer closed
  },
  toolbarIcon: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-end',
    padding: '0 8px',
    ...theme.mixins.toolbar,
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  appBarShift: {
    marginLeft: drawerWidth,
    width: `calc(100% - ${drawerWidth}px)`,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  menuButton: {
    marginRight: 36,
  },
  menuButtonHidden: {
    display: 'none',
  },
  title: {
    flexGrow: 1,
  },
  drawerPaper: {
    position: 'relative',
    whiteSpace: 'nowrap',
    width: drawerWidth,
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  drawerPaperClose: {
    overflowX: 'hidden',
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    width: theme.spacing(7),
    [theme.breakpoints.up('sm')]: {
      width: theme.spacing(9),
    },
  },
  appBarSpacer: theme.mixins.toolbar,
  content: {
    flexGrow: 1,
    height: '100vh',
    overflow: 'auto',
  },
  container: {
    paddingTop: theme.spacing(4),
    paddingBottom: theme.spacing(4),
  },
  paper: {
    padding: theme.spacing(2),
    display: 'flex',
    overflow: 'auto',
    flexDirection: 'column',
  },
  fixedHeight: {
    height: 290,
  },
}));

function Dashboard(props) {
  const classes = useStyles();
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);

  //state items needed for form
  const [guessToSubmit, setGuessToSubmit] = React.useState('');
  const [guessToSubmitError, setGuessToSubmitError] = React.useState(false);
  const [submitButtonDisabled, setSubmitButtonDisabled] = React.useState(true);
  const [guessToSubmitErrorText, setGuessToSubmitErrorText] = React.useState('');

  const { loginWithRedirect, logout,  user, isAuthenticated, isLoading, getAccessTokenSilently} = useAuth0();

  const guessSubmittedHandler = (event) => {
    event.preventDefault();
    const guess = {userName: 'Rhys', totalCases: guessToSubmit, guessDate: props.nextGuessInfo.nextGuessDate}
    props.createGuess(guess);
  }

  const onChangeGuessToSubmitHandler = (event) => {
    setGuessToSubmit(event.target.value);
    const guess = event.target.value;

    if(parseInt(guess) != null && parseInt(guess) > 0) {
      setGuessToSubmitError(false);
      setSubmitButtonDisabled(false);
      setGuessToSubmitErrorText('');
    }
    else {
      setGuessToSubmitError(true);
      setSubmitButtonDisabled(true);
      setGuessToSubmitErrorText('Please enter a valid number');
    } 
  };

  useEffect(() => {
        props.fetchDaysWon();
        props.fetchResults(6);
        props.fetchUserSubmittedNextGuess('Rhys');
  }, []); // passing an empty array as second argument triggers the callback in useEffect only after the initial render thus replicating `componentDidMount` lifecycle behaviour

  const loginButton =  <Button variant="contained"
                               color="secondary"
                               className={classes.submit}
                               onClick={() => loginWithRedirect()}>
                                  Log In
                        </Button>;

  const logoutButton =  <Button variant="contained"
                                color="secondary"
                                className={classes.submit}
                                onClick={() => logout({ returnTo: window.location.origin })}>
                                  Log Out
                        </Button>;

  let mainDiv = <Container maxWidth="lg" className={classes.container}>
                  <Grid container spacing={3}>
                    {/* Last Result */}
                    <Grid item xs={12} md={4} lg={3}>
                      <Paper className={fixedHeightPaper}>
                        {props.results ? <LastResult result={props.results[0]}/> : null}
                      </Paper>
                    </Grid>
                    {/* Days Won Pie Chart */}
                    <Grid item xs={12} md={4} lg={3}>
                      <Paper className={fixedHeightPaper}>
                        {props.daysWon ? <DaysWon results={props.daysWon}/> : null}
                      </Paper>
                    </Grid>
                    {/* Submit Guess */}
                    <Grid item xs={12} md={4} lg={3}>
                      <Paper className={fixedHeightPaper}>
                        {props.nextGuessInfo ? <SubmitGuess nextGuessInfo={props.nextGuessInfo} 
                                                            guessSubmitted={guessSubmittedHandler} 
                                                            onChangeGuess={onChangeGuessToSubmitHandler}
                                                            guessError={guessToSubmitError}
                                                            submitButtonDisabled={submitButtonDisabled}
                                                            errorText={guessToSubmitErrorText}/> : null}
                      </Paper>
                    </Grid>
                    {/* Previous Results */}
                    <Grid item xs={12}>
                      <Paper className={classes.paper}>
                        {props.results ? <PreviousResults results={props.results.slice(1)} /> : null}
                      </Paper>
                    </Grid>
                  </Grid>
                  <Box pt={4}>
                    <Copyright />
                  </Box>
                </Container>;

  if(!isAuthenticated) {
    mainDiv = <Typography component="h1">Because you really have nothing better to be doing than guessing today's covid numbers </Typography>
  }

  return (
    <div className={classes.root}>
      <CssBaseline />
      <AppBar position="absolute" className={clsx(classes.appBar, false && classes.appBarShift)}>
        <Toolbar className={classes.toolbar}>
          <Typography component="h1" variant="h6" color="inherit" noWrap className={classes.title}>
            KovidR.
          </Typography>
          <IconButton color="inherit">
            {isAuthenticated ? logoutButton : loginButton}
          </IconButton>
        </Toolbar>
      </AppBar>
      <main className={classes.content}>
        <div className={classes.appBarSpacer} />
        {mainDiv}
      </main>
    </div>
  );
}

const mapStateToProps = state => {
  return {
      daysWon: state.guess.daysWon,
      results: state.guess.results,
      loading: state.guess.loading,
      nextGuessInfo: state.guess.nextGuessInfo
  }
}

const mapDispatchToProps = dispatch => {
  return {
      fetchDaysWon: () => dispatch(actions.fetchResultsByDaysWon()),
      fetchResults: (numResultsToFetch) => dispatch(actions.fetchResults(numResultsToFetch)),
      fetchUserSubmittedNextGuess: (userName) => dispatch(actions.fetchUserSubmittedNextGuess(userName)),
      createGuess: (guess) => dispatch(actions.createGuess(guess)),
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Dashboard);
