import $axios from '../helpers/axios-instance';

//const baseUrl = process.env.API_ADMIN_URL;
const baseUrl = 'http://localhost:8000';

function getHeaders(token) {
  return { Authorization: 'Bearer ' + token,
          'Access-Control-Allow-Origin': '*',
          'Content-Type': 'application/json',
          Accept: 'application/json'  };
}

export default {

  getResultsByDaysWon(token) {
    return $axios.get(`/api/v1/Guess/GetResultsByDaysWon`, { baseURL: baseUrl, headers : getHeaders(token)});
  },
  getResultsList(numResultsToFetch) {
    return $axios.get(`api/v1/Guess/GetResultsList/${numResultsToFetch}`, { baseURL: baseUrl, headers : getHeaders(token) });
  },
  hasUserSubmittedNextGuess(userName) {
    return $axios.get(`api/v1/Guess/HasUserSubmittedNextGuess/${userName}`, { baseURL: baseUrl, headers : getHeaders(token) });
  },
  createGuess(guess) {
    return $axios.post('api/v1/Guess/CreateGuess/', guess, { baseURL: baseUrl, headers : getHeaders(token) });
  },
};
